mapper.update:
update post_comment
   set comment_contents = #{comment_contents}
 where comment_code = #{comment_code}

mapper.select:
select *
  from (select b.*, round((sysdate - comment_date) * 24 * 60) resultminute, rownum rn
          from (select post_comment.*, (select count(*)
                                   		  from good
                                  		 where division = 15) goodCount, (select profile_path
                                                                     		from users
                                                                    	   where user_id = post_comment.mem_id) profile_path,
                                                                  		 (select logo_path
                                                                     		from corporation
                                                                    	   where corp_id = post_comment.mem_id) logo_path
                  from post_comment
                 where ref_code = #{ref_code}
                   and division = #{division}
                order by comment_date desc) b)
 where rn between (#{page}-1) * #{pageSize} and #{page}*#{pageSize}
   and comment_code < #{criteria_code}

select count(*)
  from post_comment
 where ref_code = #{ref_code}
   and division = #{division}
