mapper.select:
select *
  from corporation
 where corp_id = #{corp_id}

SELECT *
  FROM CORPORATION
 WHERE CORP_NAME = #{corp_name, jdbcType=VARCHAR}

 SELECT USER_ID
	FROM CAREER_INFO
	WHERE CORPORATE_NAME = #{corporate_name }

SELECT user_id, job_position
	FROM CAREER_INFO
WHERE corp_code = #{corp_code}

SELECT count (*)
	FROM CAREER_INFO
WHERE corp_code = #{corp_code}

select *
from (SELECT job_position, count(job_position) as employee_cnt, ROW_NUMBER() OVER (ORDER BY job_position,count(job_position) desc) rn FROM CAREER_INFO WHERE corp_code = #{ corp_code } group by job_position order by count(*) desc)
where rn between ((#{page}-1) * #{pageSize}) + 1 and #{page}*#{pageSize}
