mapper.select:
select follow_code, mem_id, ref_keyword, division
  from follow
 where mem_id = #{mem_id}

select *
  from follow
 where mem_id = #{mem_id}
   and ref_keyword = #{ref_keyword}
   and division = '43'

mapper.delete:
delete from follow
 where mem_id = #{mem_id}
   and ref_keyword = #{ref_keyword}
   and division = '11'

mapper.select:
select count(*)
  from follow
 where mem_id = #{mem_id}
   and ref_keyword = #{ref_keyword}
   and division = 16
