mapper.update:
update hashtag_list
   set hashtag_name = #{hashtag_name}
 where taglist_code = #{taglist_code}
   and ref_code = #{ref_code}
   and division = #{division}

mapper.select:
select taglist_code, hashtag_name, ref_code, division, reg_date
  from hashtag_list
 where ref_code = #{ref_code}
   and division = #{division}
